20250917202218	{"-- Migration: 20250917202218_baseline_enhanced_schema.sql\n-- Created: 09/17/25 3:20PM ET\n-- Purpose: Complete enhanced schema for Claude-assisted financial data management\n-- Based on: /docs/Design/02-Technical/database-schema-enhanced.md\n-- Replaces: Phase 1 schema with full 10-table enhanced multi-entity design\n\n-- =================================================================\n-- Enhanced Database Schema - Financial Data Management System\n-- =================================================================\n\n-- -----------------------------------------------------------------\n-- TABLE: entities\n-- Purpose: Master table for all business entities and individual taxpayers\n-- -----------------------------------------------------------------\n\nCREATE TABLE entities (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_name TEXT NOT NULL,\n    entity_type TEXT NOT NULL CHECK (entity_type IN ('individual', 's_corp', 'llc', 'partnership', 'c_corp', 'trust')),\n    tax_id TEXT NOT NULL UNIQUE,\n    tax_id_display TEXT,\n    primary_taxpayer TEXT,\n    tax_year_end TEXT DEFAULT '12-31',\n    georgia_resident BOOLEAN DEFAULT TRUE,\n    entity_status TEXT DEFAULT 'active' CHECK (entity_status IN ('active', 'inactive', 'dissolved')),\n    formation_date DATE,\n    notes TEXT,\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: institutions\n-- Purpose: Financial institutions that hold accounts for entities\n-- -----------------------------------------------------------------\n\nCREATE TABLE institutions (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    institution_name TEXT NOT NULL,\n    institution_type TEXT CHECK (institution_type IN ('brokerage', 'bank', 'credit_union', 'insurance', 'retirement_plan', 'other')),\n    routing_number TEXT,\n    swift_code TEXT,\n    institution_address TEXT,\n    primary_contact JSONB,\n    login_credentials JSONB,\n    document_delivery JSONB,\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'inactive', 'closed')),\n    notes TEXT,\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: accounts\n-- Purpose: Individual financial accounts within institutions\n-- -----------------------------------------------------------------\n\nCREATE TABLE accounts (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    institution_id UUID NOT NULL REFERENCES institutions(id) ON DELETE RESTRICT,\n    account_number TEXT NOT NULL,\n    account_number_display TEXT,\n    account_name TEXT,\n    account_type TEXT NOT NULL CHECK (account_type IN ('checking', 'savings', 'brokerage', 'ira', '401k', 'roth_ira', 'trust', 'business', 'money_market', 'cd')),\n    account_subtype TEXT,\n    tax_reporting_name TEXT,\n    custodian_name TEXT,\n    account_opening_date DATE,\n    account_status TEXT DEFAULT 'active' CHECK (account_status IN ('active', 'inactive', 'closed', 'transferred')),\n    is_tax_deferred BOOLEAN DEFAULT FALSE,\n    is_tax_free BOOLEAN DEFAULT FALSE,\n    requires_rmd BOOLEAN DEFAULT FALSE,\n    beneficiary_info JSONB,\n    notes TEXT,\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: documents\n-- Purpose: Stores document metadata and extraction results\n-- -----------------------------------------------------------------\n\nCREATE TABLE documents (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    institution_id UUID NOT NULL REFERENCES institutions(id) ON DELETE RESTRICT,\n    tax_year INTEGER NOT NULL CHECK (tax_year >= 2020 AND tax_year <= 2035),\n\n    -- Document Details\n    document_type TEXT NOT NULL CHECK (document_type IN ('statement', '1099', 'quickbooks_export', 'bank_statement', 'tax_return', 'k1', 'receipt', 'invoice', 'other')),\n    period_start DATE,\n    period_end DATE,\n\n    -- File Management\n    file_path TEXT NOT NULL,\n    file_name TEXT NOT NULL,\n    file_size INTEGER,\n    file_hash TEXT NOT NULL,\n    mime_type TEXT DEFAULT 'application/pdf',\n\n    -- Amendment Tracking\n    is_amended BOOLEAN DEFAULT FALSE,\n    amends_document_id UUID REFERENCES documents(id) ON DELETE SET NULL,\n    version_number INTEGER DEFAULT 1,\n\n    -- Processing Metadata\n    processed_at TIMESTAMPTZ,\n    processed_by TEXT DEFAULT 'claude',\n    extraction_method TEXT DEFAULT 'claude_ai' CHECK (extraction_method IN ('claude_ai', 'ocr', 'manual', 'api_import')),\n    extraction_confidence TEXT NOT NULL DEFAULT 'needs_review' CHECK (extraction_confidence IN ('high', 'medium', 'low', 'needs_review', 'failed')),\n    extraction_notes TEXT,\n    needs_human_review BOOLEAN DEFAULT FALSE,\n    human_reviewed_at TIMESTAMPTZ,\n\n    -- Extraction Data\n    raw_extraction JSONB,\n    structured_data JSONB,\n    summary_data JSONB,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW(),\n    imported_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: document_accounts\n-- Purpose: Many-to-many association between documents and accounts\n-- -----------------------------------------------------------------\n\nCREATE TABLE document_accounts (\n    document_id UUID NOT NULL REFERENCES documents(id) ON DELETE CASCADE,\n    account_id UUID NOT NULL REFERENCES accounts(id) ON DELETE RESTRICT,\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    CONSTRAINT uq_document_accounts UNIQUE (document_id, account_id)\n)","-- -----------------------------------------------------------------\n-- TABLE: transactions\n-- Purpose: Individual financial transactions extracted from documents\n-- -----------------------------------------------------------------\n\nCREATE TABLE transactions (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    document_id UUID NOT NULL REFERENCES documents(id) ON DELETE CASCADE,\n    account_id UUID NOT NULL REFERENCES accounts(id) ON DELETE RESTRICT,\n\n    -- Transaction Core Data\n    transaction_date DATE NOT NULL,\n    settlement_date DATE,\n    transaction_type TEXT NOT NULL CHECK (transaction_type IN ('dividend', 'interest', 'buy', 'sell', 'transfer_in', 'transfer_out', 'fee', 'return_of_capital', 'assignment', 'other')),\n    transaction_subtype TEXT,\n    description TEXT NOT NULL,\n    amount NUMERIC(15,2) NOT NULL CHECK (amount != 0),\n    source TEXT NOT NULL CHECK (source IN ('statement', 'qb_export', 'ledger')),\n\n    -- Security Information\n    security_info JSONB,\n    security_type TEXT CHECK (security_type IN ('stock', 'bond', 'mutual_fund', 'etf', 'money_market', 'cd', 'option', 'other')),\n\n    -- Tax Categorization\n    tax_category TEXT NOT NULL CHECK (tax_category IN ('ordinary_dividend', 'qualified_dividend', 'municipal_interest', 'corporate_interest', 'capital_gain_short', 'capital_gain_long', 'return_of_capital', 'tax_exempt', 'fee_expense', 'other')),\n    federal_taxable BOOLEAN NOT NULL,\n    state_taxable BOOLEAN NOT NULL,\n    tax_details JSONB,\n\n    -- Source Tracking\n    source_transaction_id TEXT,\n    source_reference TEXT,\n\n    -- Duplicate Detection\n    is_duplicate_of UUID REFERENCES transactions(id) ON DELETE SET NULL,\n    duplicate_reason TEXT,\n\n    -- Quality Control\n    needs_review BOOLEAN DEFAULT FALSE,\n    review_notes TEXT,\n    confidence_score DECIMAL(3,2) CHECK (confidence_score >= 0 AND confidence_score <= 1),\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW(),\n    processed_by TEXT DEFAULT 'claude'\n)","-- -----------------------------------------------------------------\n-- TABLE: tax_payments\n-- Purpose: Tracks quarterly estimated tax payments and annual tax liabilities\n-- -----------------------------------------------------------------\n\nCREATE TABLE tax_payments (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    account_id UUID REFERENCES accounts(id) ON DELETE RESTRICT,\n\n    -- Payment Details\n    tax_year INTEGER NOT NULL CHECK (tax_year >= 2020 AND tax_year <= 2030),\n    payment_type TEXT NOT NULL CHECK (payment_type IN ('est_q1', 'est_q2', 'estimated_q3', 'estimated_q4', 'extension', 'balance_due', 'amended_return', 'penalty', 'interest')),\n    tax_authority TEXT NOT NULL CHECK (tax_authority IN ('federal', 'georgia', 'other_state')),\n    payment_date DATE NOT NULL,\n    due_date DATE,\n    amount NUMERIC(15,2) NOT NULL CHECK (amount > 0),\n\n    -- Calculation Details\n    calculation_basis JSONB,\n    estimated_income NUMERIC(15,2),\n    estimated_tax_liability NUMERIC(15,2),\n\n    -- Status and Review\n    notes TEXT,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: transfers\n-- Purpose: Tracks money movements between entities and accounts\n-- -----------------------------------------------------------------\n\nCREATE TABLE transfers (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n\n    -- Source Information\n    source_entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    source_account_id UUID REFERENCES accounts(id) ON DELETE RESTRICT,\n\n    -- Destination Information\n    destination_entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    destination_account_id UUID REFERENCES accounts(id) ON DELETE RESTRICT,\n\n    -- Transfer Details\n    transfer_date DATE NOT NULL,\n    amount NUMERIC(15,2) NOT NULL CHECK (amount > 0),\n    transfer_type TEXT NOT NULL CHECK (transfer_type IN ('loan', 'distribution', 'capital_contribution', 'reimbursement', 'gift', 'repayment', 'other')),\n    purpose TEXT NOT NULL,\n\n    -- Status and Review\n    notes TEXT,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW(),\n\n    -- Prevent self-transfers\n    CONSTRAINT chk_no_self_transfer CHECK (source_entity_id != destination_entity_id)\n)","-- -----------------------------------------------------------------\n-- TABLE: asset_notes\n-- Purpose: Investment strategy notes and price targets for securities\n-- -----------------------------------------------------------------\n\nCREATE TABLE asset_notes (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    account_id UUID REFERENCES accounts(id) ON DELETE RESTRICT,\n\n    -- Security Identification\n    symbol TEXT NOT NULL,\n    cusip TEXT,\n    security_name TEXT,\n    security_type TEXT CHECK (security_type IN ('stock', 'etf', 'mutual_fund', 'bond', 'option', 'other')),\n\n    -- Price Targets and Limits\n    buy_below NUMERIC(12,4) CHECK (buy_below > 0),\n    sell_above NUMERIC(12,4) CHECK (sell_above > 0),\n    stop_loss NUMERIC(12,4) CHECK (stop_loss > 0),\n    current_price NUMERIC(12,4) CHECK (current_price > 0),\n    price_updated_at TIMESTAMPTZ,\n\n    -- Performance Tracking\n    cost_basis NUMERIC(15,2),\n    current_shares NUMERIC(15,6) CHECK (current_shares >= 0),\n    unrealized_gain_loss NUMERIC(15,2),\n    last_transaction_date DATE,\n\n    -- Alerts and Monitoring\n    alert_enabled BOOLEAN DEFAULT FALSE,\n    alert_conditions JSONB,\n    dividend_yield NUMERIC(5,4) CHECK (dividend_yield >= 0),\n    next_dividend_date DATE,\n\n    -- Research and Notes\n    research_notes TEXT,\n    review_frequency TEXT CHECK (review_frequency IN ('weekly', 'monthly', 'quarterly', 'annually')),\n    next_review_date DATE,\n\n    -- Status\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'watch_list', 'sold', 'deprecated')),\n    notes TEXT,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: real_assets\n-- Purpose: Track physical properties and other non-financial assets\n-- -----------------------------------------------------------------\n\nCREATE TABLE real_assets (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n\n    -- Asset Details\n    asset_type TEXT NOT NULL CHECK (asset_type IN ('primary_residence', 'rental_property', 'commercial_property', 'land', 'vacation_home', 'vehicle', 'other')),\n    description TEXT NOT NULL,\n    address TEXT,\n    purchase_date DATE,\n    purchase_price NUMERIC(15,2),\n\n    -- Valuation\n    current_value NUMERIC(15,2) NOT NULL,\n    valuation_date DATE NOT NULL,\n    valuation_source TEXT,\n\n    -- Income/Expense\n    monthly_income NUMERIC(15,2),\n    monthly_expense NUMERIC(15,2),\n\n    -- Status\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'pending_sale', 'sold', 'transferred')),\n    notes TEXT,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- -----------------------------------------------------------------\n-- TABLE: liabilities\n-- Purpose: Track mortgages and long-term loans for net worth calculation\n-- -----------------------------------------------------------------\n\nCREATE TABLE liabilities (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    entity_id UUID NOT NULL REFERENCES entities(id) ON DELETE RESTRICT,\n    real_asset_id UUID REFERENCES real_assets(id) ON DELETE SET NULL,\n\n    -- Liability Details\n    liability_type TEXT NOT NULL CHECK (liability_type IN ('mortgage', 'home_equity', 'auto_loan', 'business_loan', 'personal_loan', 'other')),\n    lender_name TEXT NOT NULL,\n    account_number TEXT,\n\n    -- Loan Terms\n    original_amount NUMERIC(15,2) NOT NULL,\n    current_balance NUMERIC(15,2) NOT NULL,\n    interest_rate NUMERIC(5,3) NOT NULL,\n    loan_start_date DATE NOT NULL,\n    maturity_date DATE,\n\n    -- Payment Info\n    monthly_payment NUMERIC(15,2) NOT NULL,\n    next_payment_date DATE,\n    escrow_amount NUMERIC(15,2),\n\n    -- Status\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'paid_off', 'refinanced', 'transferred')),\n    notes TEXT,\n\n    -- Audit Trail\n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW()\n)","-- =================================================================\n-- INDEXES: Essential for Performance and Claude Query Patterns\n-- =================================================================\n\n-- Core entity lookups\nCREATE INDEX idx_entities_type_status ON entities(entity_type, entity_status)","CREATE INDEX idx_entities_tax_id ON entities(tax_id)","-- Institution relationships\nCREATE INDEX idx_institutions_entity ON institutions(entity_id)","CREATE INDEX idx_institutions_name_status ON institutions(institution_name, status)","-- Account management\nCREATE INDEX idx_accounts_entity ON accounts(entity_id)","CREATE INDEX idx_accounts_institution ON accounts(institution_id)","CREATE INDEX idx_accounts_type ON accounts(account_type)","CREATE INDEX idx_accounts_composite ON accounts(entity_id, institution_id, account_status)","CREATE INDEX idx_accounts_tax_attributes ON accounts(is_tax_deferred, is_tax_free, requires_rmd) WHERE account_status = 'active'","-- Document processing\nCREATE INDEX idx_documents_institution ON documents(institution_id)","CREATE UNIQUE INDEX uq_documents_file_hash ON documents(file_hash)","CREATE INDEX idx_documents_processing ON documents(extraction_confidence, needs_human_review)","CREATE INDEX idx_documents_tax_year ON documents(tax_year)","CREATE INDEX idx_documents_type_period ON documents(document_type, period_start, period_end)","CREATE INDEX idx_documents_amendments ON documents(amends_document_id) WHERE amends_document_id IS NOT NULL","-- Document-account associations\nCREATE INDEX idx_document_accounts_doc ON document_accounts(document_id)","CREATE INDEX idx_document_accounts_acct ON document_accounts(account_id)","-- Transaction analysis\nCREATE INDEX idx_transactions_entity ON transactions(entity_id)","CREATE INDEX idx_transactions_document ON transactions(document_id)","CREATE INDEX idx_transactions_account ON transactions(account_id)","CREATE INDEX idx_transactions_date ON transactions(transaction_date)","CREATE INDEX idx_transactions_date_entity ON transactions(entity_id, transaction_date)","CREATE INDEX idx_transactions_amount ON transactions(amount) WHERE ABS(amount) > 100","CREATE INDEX idx_transactions_tax_category ON transactions(tax_category)","CREATE INDEX idx_transactions_taxable ON transactions(federal_taxable, state_taxable)","CREATE INDEX idx_transactions_tax_federal ON transactions(federal_taxable, transaction_date) WHERE federal_taxable = true","CREATE INDEX idx_transactions_tax_state ON transactions(state_taxable, transaction_date) WHERE state_taxable = true","CREATE INDEX idx_transactions_security_type ON transactions(security_type) WHERE security_type IS NOT NULL","CREATE INDEX idx_transactions_review ON transactions(needs_review) WHERE needs_review = true","CREATE INDEX idx_transactions_duplicates ON transactions(is_duplicate_of) WHERE is_duplicate_of IS NOT NULL","CREATE INDEX idx_transactions_source ON transactions(source_transaction_id) WHERE source_transaction_id IS NOT NULL","-- Tax payment tracking\nCREATE INDEX idx_tax_payments_entity ON tax_payments(entity_id)","CREATE INDEX idx_tax_payments_year ON tax_payments(tax_year)","CREATE INDEX idx_tax_payments_type ON tax_payments(payment_type)","CREATE INDEX idx_tax_payments_authority ON tax_payments(tax_authority)","CREATE INDEX idx_tax_payments_date ON tax_payments(payment_date)","CREATE INDEX idx_tax_payments_due_date ON tax_payments(due_date) WHERE due_date IS NOT NULL","CREATE INDEX idx_tax_payments_entity_year ON tax_payments(entity_id, tax_year, payment_type)","-- Transfer management\nCREATE INDEX idx_transfers_source_entity ON transfers(source_entity_id)","CREATE INDEX idx_transfers_dest_entity ON transfers(destination_entity_id)","CREATE INDEX idx_transfers_date ON transfers(transfer_date)","CREATE INDEX idx_transfers_type ON transfers(transfer_type)","CREATE INDEX idx_transfers_entities ON transfers(source_entity_id, destination_entity_id, transfer_date)","-- Asset management\nCREATE INDEX idx_asset_notes_entity ON asset_notes(entity_id)","CREATE INDEX idx_asset_notes_account ON asset_notes(account_id) WHERE account_id IS NOT NULL","CREATE INDEX idx_asset_notes_symbol ON asset_notes(symbol)","CREATE INDEX idx_asset_notes_status ON asset_notes(status)","CREATE INDEX idx_asset_notes_review_date ON asset_notes(next_review_date) WHERE next_review_date IS NOT NULL","CREATE INDEX idx_asset_notes_alerts ON asset_notes(alert_enabled) WHERE alert_enabled = true","-- Real asset tracking\nCREATE INDEX idx_real_assets_entity ON real_assets(entity_id)","CREATE INDEX idx_real_assets_type ON real_assets(asset_type)","CREATE INDEX idx_real_assets_status ON real_assets(status)","-- Liability tracking\nCREATE INDEX idx_liabilities_entity ON liabilities(entity_id)","CREATE INDEX idx_liabilities_asset ON liabilities(real_asset_id) WHERE real_asset_id IS NOT NULL","CREATE INDEX idx_liabilities_type ON liabilities(liability_type)","CREATE INDEX idx_liabilities_status ON liabilities(status)","CREATE INDEX idx_liabilities_maturity ON liabilities(maturity_date) WHERE status = 'active'","-- JSONB indexes for flexible queries\nCREATE INDEX idx_documents_raw_extraction ON documents USING GIN (raw_extraction)","CREATE INDEX idx_documents_structured_data ON documents USING GIN (structured_data)","CREATE INDEX idx_documents_summary_data ON documents USING GIN (summary_data)","CREATE INDEX idx_transactions_security_info ON transactions USING GIN (security_info)","CREATE INDEX idx_transactions_tax_details ON transactions USING GIN (tax_details)","CREATE INDEX idx_tax_payments_calculation_basis ON tax_payments USING GIN (calculation_basis)","CREATE INDEX idx_asset_notes_alert_conditions ON asset_notes USING GIN (alert_conditions)","-- =================================================================\n-- TABLE CONSTRAINTS\n-- =================================================================\n\n-- Ensure tax_year_end format\nALTER TABLE entities ADD CONSTRAINT chk_tax_year_end_format\nCHECK (tax_year_end ~ '^\\\\d{2}-\\\\d{2}$')","-- Prevent conflicting tax attributes\nALTER TABLE accounts ADD CONSTRAINT chk_tax_attributes_exclusive\nCHECK (NOT (is_tax_deferred = true AND is_tax_free = true))","-- Ensure period logic\nALTER TABLE documents ADD CONSTRAINT chk_period_dates\nCHECK (period_start IS NULL OR period_end IS NULL OR period_start <= period_end)","-- Ensure amendment chain integrity\nALTER TABLE documents ADD CONSTRAINT chk_amendment_not_self\nCHECK (id != amends_document_id)","-- Prevent self-referential duplicates\nALTER TABLE transactions ADD CONSTRAINT chk_duplicate_not_self\nCHECK (id != is_duplicate_of)","-- =================================================================\n-- COMMENTS: Additional context for future developers/Claude\n-- =================================================================\n\nCOMMENT ON TABLE entities IS 'Master table for all business entities and individual taxpayers - central hub for organizational structure'","COMMENT ON TABLE institutions IS 'Financial institutions that hold accounts for entities - supports multiple institutions per entity'","COMMENT ON TABLE accounts IS 'Individual financial accounts within institutions - links entities to specific account numbers'","COMMENT ON TABLE documents IS 'Source documents with extraction metadata - supports multi-account documents via junction table'","COMMENT ON TABLE document_accounts IS 'Many-to-many association between documents and accounts - handles consolidated statements'","COMMENT ON TABLE transactions IS 'Individual financial transactions with comprehensive tax categorization and source tracking'","COMMENT ON TABLE tax_payments IS 'Quarterly estimated tax payments and annual tax liabilities for compliance tracking'","COMMENT ON TABLE transfers IS 'Inter-entity money movements and loans for multi-entity financial management'","COMMENT ON TABLE asset_notes IS 'Investment strategy notes and price targets for securities performance tracking'","COMMENT ON TABLE real_assets IS 'Physical properties and non-financial assets for complete net worth calculation'","COMMENT ON TABLE liabilities IS 'Mortgages and long-term debt for net worth tracking - excludes inter-entity loans and credit cards'","COMMENT ON COLUMN transactions.amount IS 'CRITICAL: Uses NUMERIC(15,2) to prevent floating-point errors in financial calculations'","COMMENT ON COLUMN documents.raw_extraction IS 'Complete unprocessed extraction data from Claude for debugging and reprocessing'","COMMENT ON COLUMN transactions.security_info IS 'Flexible JSONB storage: {cusip, symbol, name, quantity, price, security_type}'","COMMENT ON COLUMN documents.summary_data IS 'High-level summary data: 1099 totals, statement summaries for quick reference'","COMMENT ON COLUMN transactions.tax_details IS 'Complex tax scenarios: {issuer_state, taxpayer_state, amt_preference, section_199a}'","-- =================================================================\n-- MIGRATION COMPLETE\n-- =================================================================\n-- This migration creates the complete enhanced schema as specified in\n-- /docs/Design/02-Technical/database-schema-enhanced.md\n--\n-- Key features:\n-- - 10 table